#include <iostream>
#include <string>
#include <algorithm>

int main() {
    std::string s;
    std::getline(std::cin, s);

    // Convert to lowercase
    std::transform(s.begin(), s.end(), s.begin(), ::tolower);

    // Remove non-alphanumeric
    std::string clean;
    for(char c : s) {
        if(std::isalnum(c)) {
            clean += c;
        }
    }

    // Check palindrome
    std::string rev = clean;
    std::reverse(rev.begin(), rev.end());
    std::cout << (clean == rev ? "YES" : "NO") << std::endl;
    return 0;
}

